export const insertImportJobSchema = createInsertSchema(importJobs).omit({ 
  id: true, 
  createdAt: true 
});

export type ImportJob = typeof importJobs.$inferSelect;
export type InsertImportJob = z.infer<typeof insertImportJobSchema>;

// Auto Import Playlists schema
export const insertAutoImportPlaylistSchema = createInsertSchema(autoImportPlaylists)
  .omit({ 
    id: true, 
    createdAt: true,
    updatedAt: true
  })
  .extend({
    url: z.string().min(1, "URL Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÐµÐ½").url("Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÐºÐ¾Ñ€Ñ€ÐµÐºÑ‚Ð½Ñ‹Ð¹ URL"),
    name: z.string().min(1, "ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð¾").max(255, "ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ ÑÐ»Ð¸ÑˆÐºÐ¾Ð¼ Ð´Ð»Ð¸Ð½Ð½Ð¾Ðµ"),
    sortOrder: z.number().int().min(0, "ÐŸÐ¾Ñ€ÑÐ´Ð¾Ðº ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÐ¸ Ð´Ð¾Ð»Ð¶ÐµÐ½ Ð±Ñ‹Ñ‚ÑŒ Ð½ÐµÐ¾Ñ‚Ñ€Ð¸Ñ†Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ð¼").optional(),
    enabled: z.boolean().optional(),
    service: z.string().optional(),
    description: z.string().optional(),
    lastImportAt: z.date().nullable().optional()
