    rating?: number;
    isAnonymous?: boolean;
  };
  mode?: 'create' | 'edit';
  onSuccess?: () => void;
}

export function CommentForm({ 
  releaseId,
  initialData,
  mode = 'create',
  onSuccess
}: CommentFormProps) {
  const [text, setText] = useState(initialData?.text || "");
