  const displayName = comment.isAnonymous ? "ÐÐ½Ð¾Ð½Ð¸Ð¼" : comment.user?.nickname || "ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹";

