    mutationFn: async () => {
      await apiRequest('POST', `/api/comments/${comment.id}/report`, { 
        reason: 'Inappropriate content' 
      });
    },
    onSuccess: () => {
      toast({ title: "Ð–Ð°Ð»Ð¾Ð±Ð° Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð°" });
    },
