import { storage } from "./storage";
import type { InsertArtist, InsertRelease } from "@shared/schema";

// Music Import System Ð´Ð»Ñ RevYou
// Ð›ÐµÐ³Ð°Ð»ÑŒÐ½Ñ‹Ð¹ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚ Ð¼ÐµÑ‚Ð°Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¸Ð· Ð¾Ñ‚ÐºÑ€Ñ‹Ñ‚Ñ‹Ñ… Ð¸ÑÑ‚Ð¾Ñ‡Ð½Ð¸ÐºÐ¾Ð²

interface ImportedRelease {
  artist: string;
  album: string;
  releaseDate: string;
  coverUrl?: string;
  spotifyUrl?: string;
  genres?: string[];
  trackCount?: number;
}

interface MusicBrainzArtist {
  id: string;
  name: string;
  country?: string;
  'life-span'?: {
    begin?: string;
    end?: string;
  };
}

interface MusicBrainzRelease {
  id: string;
  title: string;
  date?: string;
  country?: string;
  'cover-art-archive'?: {
    artwork: boolean;
    front: boolean;
  };
  'media'?: Array<{
    'track-count': number;
  }>;
}

/**
 * MusicBrainz API Client
 * ÐŸÐ¾Ð»Ð½Ð¾ÑÑ‚ÑŒÑŽ Ð±ÐµÑÐ¿Ð»Ð°Ñ‚Ð½Ñ‹Ð¹ Ð¸ÑÑ‚Ð¾Ñ‡Ð½Ð¸Ðº Ð¼ÑƒÐ·Ñ‹ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð¼ÐµÑ‚Ð°Ð´Ð°Ð½Ð½Ñ‹Ñ…
 */
export class MusicBrainzImporter {
  private readonly baseUrl = 'https://musicbrainz.org/ws/2';
  private readonly userAgent = 'RevYou/1.0 (music@revyou.app)';

  private async makeRequest(endpoint: string): Promise<any> {
    // Ð¡Ð¾Ð±Ð»ÑŽÐ´Ð°ÐµÐ¼ rate limit - 1 Ð·Ð°Ð¿Ñ€Ð¾Ñ Ð² ÑÐµÐºÑƒÐ½Ð´Ñƒ
    await new Promise(resolve => setTimeout(resolve, 1100));

    const response = await fetch(`${this.baseUrl}${endpoint}`, {
      headers: {
        'User-Agent': this.userAgent,
        'Accept': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error(`MusicBrainz API error: ${response.status}`);
    }

    return response.json();
  }

  /**
   * ÐŸÐ¾Ð¸ÑÐº Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ñ Ð¿Ð¾ Ð¸Ð¼ÐµÐ½Ð¸
   */
  async searchArtist(artistName: string): Promise<MusicBrainzArtist | null> {
    try {
      const query = encodeURIComponent(`artist:"${artistName}"`);
      const data = await this.makeRequest(`/artist/?query=${query}&fmt=json&limit=1`);

      if (data.artists && data.artists.length > 0) {
        return data.artists[0];
      }
      return null;
    } catch (error) {
      console.error('Error searching artist:', error);
      return null;
    }
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð²ÑÐµÑ… Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ñ
   */
  async getArtistReleases(artistMbid: string): Promise<MusicBrainzRelease[]> {
    try {
      const data = await this.makeRequest(`/release-group/?artist=${artistMbid}&type=album&fmt=json&limit=100`);

      if (data['release-groups']) {
        // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð´ÐµÑ‚Ð°Ð»Ð¸ ÐºÐ°Ð¶Ð´Ð¾Ð³Ð¾ Ñ€ÐµÐ»Ð¸Ð·Ð°
        const releases: MusicBrainzRelease[] = [];

        for (const group of data['release-groups']) {
          const releaseData = await this.makeRequest(`/release?release-group=${group.id}&fmt=json&limit=1&inc=media`);
          if (releaseData.releases && releaseData.releases.length > 0) {
            releases.push(releaseData.releases[0]);
          }
        }

        return releases;
      }
      return [];
    } catch (error) {
      console.error('Error getting artist releases:', error);
      return [];
    }
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ URL Ð¾Ð±Ð»Ð¾Ð¶ÐºÐ¸ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð°
   */
  async getCoverArtUrl(releaseMbid: string): Promise<string | null> {
    try {
      // Cover Art Archive API - Ñ‚Ð°ÐºÐ¶Ðµ Ð±ÐµÑÐ¿Ð»Ð°Ñ‚Ð½Ñ‹Ð¹
      const response = await fetch(`https://coverartarchive.org/release/${releaseMbid}/front-500`);
      if (response.ok) {
        return response.url;
      }
      return null;
    } catch (error) {
      return null;
    }
  }

  /**
   * Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ñ Ð² Ð½Ð°ÑˆÑƒ Ð‘Ð”
   */
  async importArtistReleases(artistName: string): Promise<ImportedRelease[]> {
    console.log(`ðŸŽµ Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð²: ${artistName}`);

    // 1. ÐÐ°Ð¹Ñ‚Ð¸ Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ñ
    const artist = await this.searchArtist(artistName);
    if (!artist) {
      console.log(`âŒ Ð˜ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½: ${artistName}`);
      return [];
    }

    console.log(`âœ… ÐÐ°Ð¹Ð´ÐµÐ½ Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒ: ${artist.name} (${artist.id})`);

    // 2. ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð²ÑÐµ Ñ€ÐµÐ»Ð¸Ð·Ñ‹
    const releases = await this.getArtistReleases(artist.id);
    console.log(`ðŸ“€ ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð²: ${releases.length}`);

    // 3. ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ð°Ñ‚ÑŒ ÐºÐ°Ð¶Ð´Ñ‹Ð¹ Ñ€ÐµÐ»Ð¸Ð·
    const importedReleases: ImportedRelease[] = [];

    for (const release of releases) {
      try {
        // ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¾Ð±Ð»Ð¾Ð¶ÐºÑƒ
        const coverUrl = await this.getCoverArtUrl(release.id);

        const importedRelease: ImportedRelease = {
          artist: artist.name,
          album: release.title,
          releaseDate: release.date || new Date().toISOString(),
          coverUrl: coverUrl || undefined,
          trackCount: release.media?.[0]?.['track-count']
        };

        importedReleases.push(importedRelease);
        console.log(`ðŸ’½ Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½: ${release.title}`);

      } catch (error) {
        console.error(`âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð° Ñ€ÐµÐ»Ð¸Ð·Ð° ${release.title}:`, error);
      }
    }

    return importedReleases;
  }

  /**
   * ÐŸÐ¾Ð¸ÑÐº Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð¿Ð¾ Ð³Ð¾Ð´Ñƒ Ð²Ñ‹Ð¿ÑƒÑÐºÐ°
   */
  async getReleasesByYear(year: number): Promise<MusicBrainzRelease[]> {
    try {
      console.log(`ðŸ” ÐŸÐ¾Ð¸ÑÐº Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð·Ð° ${year} Ð³Ð¾Ð´...`);

      // ÐŸÐ¾Ð¸ÑÐº Ð¿Ð¾Ð¿ÑƒÐ»ÑÑ€Ð½Ñ‹Ñ… Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð·Ð° Ð¾Ð¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð½Ñ‹Ð¹ Ð³Ð¾Ð´
      const query = `date:${year} AND type:album`;
      const data = await this.makeRequest(`/release/?query=${encodeURIComponent(query)}&fmt=json&limit=50`);

      if (data.releases && data.releases.length > 0) {
        console.log(`ðŸ“€ ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ ${data.releases.length} Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð·Ð° ${year} Ð³Ð¾Ð´`);
        return data.releases.filter((release: any) => 
          release.date && release.date.includes(year.toString())
        );
      }

      return [];
    } catch (error) {
      console.error(`Error getting releases by year ${year}:`, error);
      return [];
    }
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÑŽ Ð¾Ð± Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ðµ Ð¿Ð¾ ID Ñ€ÐµÐ»Ð¸Ð·Ð°
   */
  async getArtistFromRelease(releaseMbid: string): Promise<MusicBrainzArtist | null> {
    try {
      const data = await this.makeRequest(`/release/${releaseMbid}?fmt=json&inc=artists`);

      if (data['artist-credit'] && data['artist-credit'].length > 0) {
        return data['artist-credit'][0].artist;
      }

      return null;
    } catch (error) {
      console.error('Error getting artist from release:', error);
      return null;
    }
  }

  /**
   * Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð¿Ð¾ Ð³Ð¾Ð´Ñƒ Ð²Ñ‹Ð¿ÑƒÑÐºÐ°
   */
  async importReleasesByYear(year: number): Promise<ImportedRelease[]> {
    console.log(`ðŸ“… Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² ${year} Ð³Ð¾Ð´Ð°...`);

    // 1. ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ñ€ÐµÐ»Ð¸Ð·Ñ‹ Ð·Ð° ÑƒÐºÐ°Ð·Ð°Ð½Ð½Ñ‹Ð¹ Ð³Ð¾Ð´
    const releases = await this.getReleasesByYear(year);
    console.log(`ðŸ“€ ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð²: ${releases.length}`);

    if (releases.length === 0) {
      return [];
    }

    // 2. ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ð°Ñ‚ÑŒ ÐºÐ°Ð¶Ð´Ñ‹Ð¹ Ñ€ÐµÐ»Ð¸Ð·
    const importedReleases: ImportedRelease[] = [];

    for (const release of releases) {
      try {
        // ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÑŽ Ð¾Ð± Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ðµ
        const artist = await this.getArtistFromRelease(release.id);
        if (!artist) {
          console.log(`âš ï¸ ÐÐµ Ð½Ð°Ð¹Ð´ÐµÐ½ Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒ Ð´Ð»Ñ Ñ€ÐµÐ»Ð¸Ð·Ð°: ${release.title}`);
          continue;
        }

        // ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¾Ð±Ð»Ð¾Ð¶ÐºÑƒ
        const coverUrl = await this.getCoverArtUrl(release.id);

        const importedRelease: ImportedRelease = {
          artist: artist.name,
          album: release.title,
          releaseDate: release.date || `${year}-01-01`,
          coverUrl: coverUrl || undefined,
          trackCount: release.media?.[0]?.['track-count']
        };

        importedReleases.push(importedRelease);
        console.log(`ðŸ’½ Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½: ${artist.name} - ${release.title}`);

      } catch (error) {
        console.error(`âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð° Ñ€ÐµÐ»Ð¸Ð·Ð° ${release.title}:`, error);
      }
    }

    return importedReleases;
  }
}

/**
 * Ð¡ÐµÑ€Ð²Ð¸Ñ Ð¼Ð°ÑÑÐ¾Ð²Ð¾Ð³Ð¾ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð°
 */
export class MassImportService {
  private musicBrainz = new MusicBrainzImporter();

  /**
   * Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ð½ÐµÑÐºÐ¾Ð»ÑŒÐºÐ¸Ñ… Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÐµÐ¹
   */
  async importArtists(artistNames: string[]): Promise<{ success: number; errors: string[] }> {
    let successCount = 0;
    const errors: string[] = [];

    for (const artistName of artistNames) {
      try {
        // 1. Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ Ñ€ÐµÐ»Ð¸Ð·Ñ‹ Ð¸Ð· MusicBrainz
        const releases = await this.musicBrainz.importArtistReleases(artistName);

        // 2. Ð¡Ð¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð² Ð½Ð°ÑˆÑƒ Ð‘Ð”
        for (const release of releases) {
          await this.saveReleaseToDatabase(release);
        }

        successCount += releases.length;
        console.log(`âœ… Ð£ÑÐ¿ÐµÑˆÐ½Ð¾ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¾ ${releases.length} Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð´Ð»Ñ ${artistName}`);

      } catch (error) {
        const errorMsg = `ÐžÑˆÐ¸Ð±ÐºÐ° Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð° ${artistName}: ${error}`;
        errors.push(errorMsg);
        console.error(errorMsg);
      }
    }

    return { success: successCount, errors };
  }

  /**
   * Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð¿Ð¾ Ð³Ð¾Ð´Ð°Ð¼ Ð²Ñ‹Ð¿ÑƒÑÐºÐ°
   */
  async importByYears(years: number[]): Promise<{ success: number; errors: string[] }> {
    let successCount = 0;
    const errors: string[] = [];

    for (const year of years) {
      try {
        console.log(`ðŸ—“ï¸ Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚ Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² ${year} Ð³Ð¾Ð´Ð°...`);

        // 1. ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ñ€ÐµÐ»Ð¸Ð·Ñ‹ Ð¸Ð· MusicBrainz Ð¿Ð¾ Ð³Ð¾Ð´Ñƒ
        const releases = await this.musicBrainz.importReleasesByYear(year);

        // 2. Ð¡Ð¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð² Ð½Ð°ÑˆÑƒ Ð‘Ð”
        for (const release of releases) {
          await this.saveReleaseToDatabase(release);
        }

        successCount += releases.length;
        console.log(`âœ… Ð£ÑÐ¿ÐµÑˆÐ½Ð¾ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¾ ${releases.length} Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ð·Ð° ${year} Ð³Ð¾Ð´`);

      } catch (error) {
        const errorMsg = `ÐžÑˆÐ¸Ð±ÐºÐ° Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð° Ð·Ð° ${year} Ð³Ð¾Ð´: ${error}`;
        errors.push(errorMsg);
        console.error(errorMsg);
      }
    }

    return { success: successCount, errors };
  }

  /**
   * Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ Ñ€ÐµÐ»Ð¸Ð·Ð° Ð² Ð‘Ð”
   */
  private async saveReleaseToDatabase(release: ImportedRelease): Promise<void> {
    try {
      // Ð¡Ð¾Ð·Ð´Ð°Ñ‚ÑŒ Ð¸Ð»Ð¸ Ð½Ð°Ð¹Ñ‚Ð¸ Ð¸ÑÐ¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»Ñ
      let artist = await storage.getArtistByName(release.artist);
      if (!artist) {
        const artistData: InsertArtist = {
          name: release.artist,
        };
        artist = await storage.createArtist(artistData);
      }

      // ÐŸÑ€Ð¾Ð²ÐµÑ€Ð¸Ñ‚ÑŒ, ÐµÑÑ‚ÑŒ Ð»Ð¸ ÑƒÐ¶Ðµ Ñ‚Ð°ÐºÐ¾Ð¹ Ñ€ÐµÐ»Ð¸Ð·
      const existingRelease = await storage.getReleaseByTitleAndArtist(release.album, artist.id);
      if (existingRelease) {
        console.log(`âš ï¸ Ð ÐµÐ»Ð¸Ð· ÑƒÐ¶Ðµ ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÐµÑ‚: ${release.album}`);
        return;
      }

      // Ð¡Ð¾Ð·Ð´Ð°Ñ‚ÑŒ Ð½Ð¾Ð²Ñ‹Ð¹ Ñ€ÐµÐ»Ð¸Ð·
      const releaseData: InsertRelease = {
        artistId: artist.id,
        title: release.album,
        releaseDate: new Date(release.releaseDate),
        coverUrl: release.coverUrl || null,
        streamingLinks: release.spotifyUrl ? {
          spotify: release.spotifyUrl,
          appleMusic: null
        } : null,
        type: 'album'
      };

      await storage.createRelease(releaseData);
      console.log(`ðŸ’¾ Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½ Ñ€ÐµÐ»Ð¸Ð·: ${release.album}`);

    } catch (error) {
      console.error(`âŒ ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ ${release.album}:`, error);
      throw error;
    }
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÑƒ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ð°
   */
  async getImportStats(): Promise<{ totalReleases: number; realReleases: number; testReleases: number; totalArtists: number }> {
    const allReleases = await storage.getReleases({ includeTestData: true });
    const realReleases = await storage.getReleases({ includeTestData: false });
    const artists = await storage.getArtists();

    return {
      totalReleases: allReleases.length,
      realReleases: realReleases.length,
      testReleases: allReleases.length - realReleases.length,
      totalArtists: artists.length
    };
  }
}

// Ð­ÐºÑÐ¿Ð¾Ñ€Ñ‚ Ð´Ð»Ñ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ð½Ð¸Ñ Ð² Ñ€Ð¾ÑƒÑ‚Ð°Ñ…
export const massImportService = new MassImportService();

