      // Rate limiting between artists
      await new Promise(resolve => setTimeout(resolve, 2000));
    }

    console.log(`âœ… Import completed. New releases: ${stats.newReleases}, Skipped: ${stats.skippedReleases}, Errors: ${stats.errors.length}`);

    return stats;

  } catch (error) {
    console.error('âŒ Error during playlist import:', error);
    stats.errors.push(`Import failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    return stats;
  }
}

// ÐžÐ±Ñ€Ð°Ñ‚Ð½Ð°Ñ ÑÐ¾Ð²Ð¼ÐµÑÑ‚Ð¸Ð¼Ð¾ÑÑ‚ÑŒ
export async function importFromYandexPlaylist(playlistUrl: string): Promise<ImportStats> {
