// ÐšÐ¾Ð¼Ð±Ð¸Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ð¹ Ð¼ÑƒÐ·Ñ‹ÐºÐ°Ð»ÑŒÐ½Ñ‹Ð¹ API ÐºÐ»Ð¸ÐµÐ½Ñ‚
// Deezer API (Ð¾ÑÐ½Ð¾Ð²Ð½Ð¾Ð¹) + iTunes API (fallback)
// ÐœÐ°ÐºÑÐ¸Ð¼Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¿Ð¾ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ñ€Ð¾ÑÑÐ¸Ð¹ÑÐºÐ¸Ñ… Ð¸ Ð¼ÐµÐ¶Ð´ÑƒÐ½Ð°Ñ€Ð¾Ð´Ð½Ñ‹Ñ… Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð¾Ð²

// Ð˜Ð½Ñ‚ÐµÑ€Ñ„ÐµÐ¹ÑÑ‹ Ð´Ð»Ñ ÑƒÐ½Ð¸Ñ„Ð¸Ñ†Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ…
export interface UnifiedArtist {
  id: string;
  name: string;
  imageUrl?: string;
  genres?: string[];
  popularity?: number;
  source: 'deezer' | 'itunes';
}

export interface UnifiedAlbum {
  id: string;
  title: string;
  releaseDate?: string;
  albumType: 'album' | 'single' | 'compilation';
  trackCount?: number;
  imageUrl?: string;
  source: 'deezer' | 'itunes';
}

// Deezer API ÐºÐ»Ð¸ÐµÐ½Ñ‚
class DeezerAPIClient {
  private readonly baseUrl = 'https://api.deezer.com';

  async searchArtist(artistName: string): Promise<UnifiedArtist | null> {
    try {
      console.log(`ðŸŸ¡ Deezer: Ð˜Ñ‰ÐµÐ¼ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° "${artistName}"`);

      const response = await fetch(`${this.baseUrl}/search/artist?q=${encodeURIComponent(artistName)}&limit=1`);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (!data.data || data.data.length === 0) {
        console.log(`ðŸŸ¡ Deezer: ÐÑ€Ñ‚Ð¸ÑÑ‚ "${artistName}" Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½`);
        return null;
      }

      const artist = data.data[0];

      console.log(`ðŸŸ¡ Deezer: ÐÐ°Ð¹Ð´ÐµÐ½ "${artist.name}" (ID: ${artist.id})`);

      return {
        id: artist.id.toString(),
        name: artist.name,
        imageUrl: artist.picture_medium || artist.picture,
        popularity: artist.nb_fan || 0,
        source: 'deezer'
      };

    } catch (error) {
      console.error(`ðŸŸ¡ Deezer error Ð´Ð»Ñ "${artistName}":`, error instanceof Error ? error.message : String(error));
      return null;
    }
  }

  async getArtistAlbums(artistId: string): Promise<UnifiedAlbum[]> {
    try {
      console.log(`ðŸŸ¡ Deezer: ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ñ‹ Ð´Ð»Ñ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° ${artistId}`);

      // ÐžÑÐ½Ð¾Ð²Ð½Ð¾Ð¹ Ð·Ð°Ð¿Ñ€Ð¾Ñ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð¾Ð² Ñ Ð¼Ð°ÐºÑÐ¸Ð¼Ð°Ð»ÑŒÐ½Ñ‹Ð¼ Ð»Ð¸Ð¼Ð¸Ñ‚Ð¾Ð¼
      const response = await fetch(`${this.baseUrl}/artist/${artistId}/albums?limit=500`);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      const albums: UnifiedAlbum[] = [];

      if (data.data) {
        albums.push(...data.data.map((album: any) => ({
          id: album.id.toString(),
          title: album.title,
          releaseDate: album.release_date,
          albumType: this.mapDeezerAlbumType(album.record_type),
          trackCount: album.nb_tracks,
          imageUrl: album.cover_medium || album.cover,
          source: 'deezer' as const
        })));
      }

      // Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ð¹ Ð¿Ð¾Ð¸ÑÐº Ð¿Ð¾ÑÐ»ÐµÐ´Ð½Ð¸Ñ… Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ñ‡ÐµÑ€ÐµÐ· search API
      try {
        const artistResponse = await fetch(`${this.baseUrl}/artist/${artistId}`);
        if (artistResponse.ok) {
          const artistData = await artistResponse.json();
          const artistName = artistData.name;

          // ÐŸÐ¾Ð¸ÑÐº Ð¿Ð¾ÑÐ»ÐµÐ´Ð½Ð¸Ñ… Ñ€ÐµÐ»Ð¸Ð·Ð¾Ð² Ñ‡ÐµÑ€ÐµÐ· search API
          const searchResponse = await fetch(`${this.baseUrl}/search/album?q=artist:"${encodeURIComponent(artistName)}"&limit=50`);
          if (searchResponse.ok) {
            const searchData = await searchResponse.json();
            if (searchData.data) {
              const existingIds = new Set(albums.map(a => a.id));

              // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð½Ð°Ð¹Ð´ÐµÐ½Ð½Ñ‹Ðµ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ñ‹, ÐºÐ¾Ñ‚Ð¾Ñ€Ñ‹Ñ… ÐµÑ‰Ðµ Ð½ÐµÑ‚
              searchData.data
                .filter((album: any) => album.artist && album.artist.id.toString() === artistId && !existingIds.has(album.id.toString()))
                .forEach((album: any) => {
                  albums.push({
                    id: album.id.toString(),
                    title: album.title,
                    releaseDate: album.release_date,
                    albumType: this.mapDeezerAlbumType(album.record_type),
                    trackCount: album.nb_tracks,
                    imageUrl: album.cover_medium || album.cover,
                    source: 'deezer' as const
                  });
                });
            }
          }
        }
      } catch (searchError) {
        console.log(`ðŸŸ¡ Deezer: Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ð¹ Ð¿Ð¾Ð¸ÑÐº Ð½Ðµ ÑƒÐ´Ð°Ð»ÑÑ Ð´Ð»Ñ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° ${artistId}`);
      }

      // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð´ÑƒÐ±Ð»Ð¸ÐºÐ°Ñ‚Ñ‹ Ð¸ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ð¿Ð¾ Ð´Ð°Ñ‚Ðµ Ð²Ñ‹Ñ…Ð¾Ð´Ð°
      const uniqueAlbums = albums.filter((album, index, self) => 
        index === self.findIndex(a => a.id === album.id)
      ).sort((a, b) => new Date(b.releaseDate || '1900-01-01').getTime() - new Date(a.releaseDate || '1900-01-01').getTime());

      console.log(`ðŸŸ¡ Deezer: ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ ${uniqueAlbums.length} ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð¾Ð²`);
      return uniqueAlbums;

    } catch (error) {
      console.error(`ðŸŸ¡ Deezer error Ð¿Ñ€Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ð¸ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð¾Ð² Ð´Ð»Ñ ${artistId}:`, error instanceof Error ? error.message : String(error));
      return [];
    }
  }

  private mapDeezerAlbumType(recordType?: string): 'album' | 'single' | 'compilation' {
    if (!recordType) return 'album';

    const type = recordType.toLowerCase();
    if (type.includes('single') || type.includes('ep')) return 'single';
    if (type.includes('compilation') || type.includes('best')) return 'compilation';
    return 'album';
  }
}

// iTunes API ÐºÐ»Ð¸ÐµÐ½Ñ‚
class ITunesAPIClient {
  private readonly baseUrl = 'https://itunes.apple.com';

  async searchArtist(artistName: string): Promise<UnifiedArtist | null> {
    try {
      console.log(`ðŸŽ iTunes: Ð˜Ñ‰ÐµÐ¼ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° "${artistName}"`);

      const response = await fetch(
        `${this.baseUrl}/search?term=${encodeURIComponent(artistName)}&entity=allArtist&attribute=allArtistTerm&limit=1`
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (!data.results || data.results.length === 0) {
        console.log(`ðŸŽ iTunes: ÐÑ€Ñ‚Ð¸ÑÑ‚ "${artistName}" Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½`);
        return null;
      }

      const artist = data.results[0];

      console.log(`ðŸŽ iTunes: ÐÐ°Ð¹Ð´ÐµÐ½ "${artist.artistName}" (ID: ${artist.artistId})`);

      return {
        id: artist.artistId.toString(),
        name: artist.artistName,
        genres: artist.primaryGenreName ? [artist.primaryGenreName] : undefined,
        source: 'itunes'
      };

    } catch (error) {
      console.error(`ðŸŽ iTunes error Ð´Ð»Ñ "${artistName}":`, error instanceof Error ? error.message : String(error));
      return null;
    }
  }

  async getArtistAlbums(artistId: string): Promise<UnifiedAlbum[]> {
    try {
      console.log(`ðŸŽ iTunes: ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ñ‹ Ð´Ð»Ñ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° ${artistId}`);

      const response = await fetch(
        `${this.baseUrl}/lookup?id=${artistId}&entity=album&limit=200`
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (!data.results || data.results.length <= 1) {
        return [];
      }

      // ÐŸÐµÑ€Ð²Ñ‹Ð¹ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚ - ÑÑ‚Ð¾ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾Ð± Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ðµ, Ð¾ÑÑ‚Ð°Ð»ÑŒÐ½Ñ‹Ðµ - Ð°Ð»ÑŒÐ±Ð¾Ð¼Ñ‹
      const albums: UnifiedAlbum[] = data.results.slice(1).map((album: any) => ({
        id: album.collectionId.toString(),
        title: album.collectionName,
        releaseDate: album.releaseDate ? album.releaseDate.split('T')[0] : undefined,
        albumType: this.mapItunesAlbumType(album.collectionType),
        trackCount: album.trackCount,
        imageUrl: album.artworkUrl100 || album.artworkUrl60,
        source: 'itunes' as const
      }));

      console.log(`ðŸŽ iTunes: ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ ${albums.length} Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð¾Ð²`);
      return albums;

    } catch (error) {
      console.error(`ðŸŽ iTunes error Ð¿Ñ€Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ð¸ Ð°Ð»ÑŒÐ±Ð¾Ð¼Ð¾Ð² Ð´Ð»Ñ ${artistId}:`, error instanceof Error ? error.message : String(error));
      return [];
    }
  }

  // ÐŸÐ¾Ð¸ÑÐº ÐºÐ¾Ð½ÐºÑ€ÐµÑ‚Ð½Ð¾Ð³Ð¾ Ñ€ÐµÐ»Ð¸Ð·Ð° Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð´Ð°Ñ‚Ñ‹ Ð²Ñ‹Ñ…Ð¾Ð´Ð°
  async searchReleaseDate(artistName: string, releaseTitle: string): Promise<string | null> {
    try {
      console.log(`ðŸŽ iTunes: Ð˜Ñ‰ÐµÐ¼ Ð´Ð°Ñ‚Ñƒ Ð´Ð»Ñ "${releaseTitle}" Ð¾Ñ‚ "${artistName}"`);

      const query = `${artistName} ${releaseTitle}`;
      const response = await fetch(
        `${this.baseUrl}/search?term=${encodeURIComponent(query)}&entity=album&limit=10`
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (!data.results || data.results.length === 0) {
        console.log(`ðŸŽ iTunes: Ð ÐµÐ»Ð¸Ð· "${releaseTitle}" Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½`);
        return null;
      }

      // Ð˜Ñ‰ÐµÐ¼ Ð½Ð°Ð¸Ð±Ð¾Ð»ÐµÐµ Ð¿Ð¾Ð´Ñ…Ð¾Ð´ÑÑ‰Ð¸Ð¹ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚
      const bestMatch = data.results.find((album: any) => {
        const titleMatch = album.collectionName?.toLowerCase().includes(releaseTitle.toLowerCase()) ||
                          releaseTitle.toLowerCase().includes(album.collectionName?.toLowerCase());
        const artistMatch = album.artistName?.toLowerCase().includes(artistName.toLowerCase()) ||
                           artistName.toLowerCase().includes(album.artistName?.toLowerCase());
        return titleMatch && artistMatch;
      });

      if (bestMatch && bestMatch.releaseDate) {
        const releaseDate = bestMatch.releaseDate.split('T')[0];
