}

// Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ð¹ Ð¿Ð°Ñ€ÑÐµÑ€ Ð´Ð»Ñ Ñ€Ð¾ÑÑÐ¸Ð¹ÑÐºÐ¸Ñ… ÑÐµÑ€Ð²Ð¸ÑÐ¾Ð²
abstract class RussianMusicParser {
  abstract serviceName: string;
  abstract baseUrl: string;

  // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° Ð¸Ð¼ÐµÐ½Ð¸ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð° Ð¾Ñ‚ Ð»Ð¸ÑˆÐ½Ð¸Ñ… ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¾Ð²
  protected cleanArtistName(artist: string): string {
    return artist
      .trim()
      .replace(/^feat\.?\s*/i, '') // Ð£Ð±Ð¸Ñ€Ð°ÐµÐ¼ "feat" Ð² Ð½Ð°Ñ‡Ð°Ð»Ðµ
      .replace(/\s*feat\.?\s*.+$/i, '') // Ð£Ð±Ð¸Ñ€Ð°ÐµÐ¼ Ð²ÑÑ‘ Ð¿Ð¾ÑÐ»Ðµ "feat"
      .replace(/\s*\(.*\)$/, '') // Ð£Ð±Ð¸Ñ€Ð°ÐµÐ¼ ÑÐºÐ¾Ð±ÐºÐ¸ Ð² ÐºÐ¾Ð½Ñ†Ðµ
      .replace(/\s+/g, ' ') // ÐÐ¾Ñ€Ð¼Ð°Ð»Ð¸Ð·ÑƒÐµÐ¼ Ð¿Ñ€Ð¾Ð±ÐµÐ»Ñ‹
      .trim();
  }

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð¾Ð² Ð¸Ð· Ñ‚Ñ€ÐµÐºÐ¾Ð²
  protected extractUniqueArtists(tracks: ParsedTrack[]): string[] {
    const artistSet = new Set<string>();

    for (const track of tracks) {
      const cleanedArtist = this.cleanArtistName(track.artist);

      if (cleanedArtist && cleanedArtist.length > 1) {
        artistSet.add(cleanedArtist);

        // ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ ÐºÐ¾Ð»Ð»Ð°Ð±Ð¾Ñ€Ð°Ñ†Ð¸Ð¸ (feat, &, ,)
        const collaborators = this.extractCollaborators(cleanedArtist);
        collaborators.forEach(collaborator => {
          if (collaborator.length > 1) {
            artistSet.add(collaborator);
          }
        });
      }
    }

    return Array.from(artistSet).sort();
  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ ÐºÐ¾Ð»Ð»Ð°Ð±Ð¾Ñ€Ð°Ñ‚Ð¾Ñ€Ð¾Ð² Ð¸Ð· Ð¸Ð¼ÐµÐ½Ð¸ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð°
  private extractCollaborators(artistName: string): string[] {
    const separators = [' feat. ', ' feat ', ' ft. ', ' ft ', ' & ', ', ', ' x ', ' X '];
    let artists = [artistName];

    for (const separator of separators) {
      artists = artists.flatMap(artist => 
        artist.split(separator).map(a => this.cleanArtistName(a))
      );
    }

    return artists.filter(artist => artist && artist.length > 1);
  }

  abstract parsePlaylist(url: string): Promise<PlaylistParseResult>;
}

// ÐŸÐ°Ñ€ÑÐµÑ€ MTS Music
export class MTSMusicParser extends RussianMusicParser {
  serviceName = 'MTS Music';
  baseUrl = 'https://music.mts.ru';

  async parsePlaylist(url: string): Promise<PlaylistParseResult> {
    console.log(`ðŸŽµ MTS Music: ÐŸÐ°Ñ€ÑÐ¸Ð¼ Ð¿Ð»ÐµÐ¹Ð»Ð¸ÑÑ‚ ${url}`);

    try {
      const response = await fetch(url, {
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
          'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
          'Accept-Language': 'ru-RU,ru;q=0.9,en;q=0.8',
          'Accept-Encoding': 'gzip, deflate, br',
          'DNT': '1',
          'Connection': 'keep-alive',
          'Upgrade-Insecure-Requests': '1',
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const html = await response.text();
      const $ = cheerio.load(html);

      // ÐŸÑ‹Ñ‚Ð°ÐµÐ¼ÑÑ Ð¾Ð¿Ñ€ÐµÐ´ÐµÐ»Ð¸Ñ‚ÑŒ Ñ‚Ð¸Ð¿ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñ‹ Ð¸ Ð¸Ð·Ð²Ð»ÐµÑ‡ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ
      let result: PlaylistParseResult;

      if (this.isChartPage(url)) {
        result = this.parseChart($, url);
      } else {
        result = this.parsePlaylistPage($, url);
      }

      console.log(`ðŸŽµ MTS Music: ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ ${result.tracks.length} Ñ‚Ñ€ÐµÐºÐ¾Ð², ${result.uniqueArtists.length} ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð¾Ð²`);

      return result;

    } catch (error) {
      console.error(`âŒ MTS Music Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³ error Ð´Ð»Ñ ${url}:`, error instanceof Error ? error.message : String(error));

      return {
        name: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð°',
        tracks: [],
        uniqueArtists: []
      };
    }
  }

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ°, ÑÐ²Ð»ÑÐµÑ‚ÑÑ Ð»Ð¸ URL Ñ‡Ð°Ñ€Ñ‚Ð¾Ð¼
  private isChartPage(url: string): boolean {
    return url.includes('/chart') || url.includes('/top') || url.includes('/hit');
  }

  // ÐŸÐ°Ñ€ÑÐ¸Ð½Ð³ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñ‹ Ñ‡Ð°Ñ€Ñ‚Ð°
  private parseChart($: cheerio.CheerioAPI, url: string): PlaylistParseResult {
    console.log('ðŸ“Š ÐŸÐ°Ñ€ÑÐ¸Ð¼ ÐºÐ°Ðº Ñ‡Ð°Ñ€Ñ‚...');

    const tracks: ParsedTrack[] = [];

    // Ð’Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ñ‹Ðµ ÑÐµÐ»ÐµÐºÑ‚Ð¾Ñ€Ñ‹ Ð´Ð»Ñ Ñ‚Ñ€ÐµÐºÐ¾Ð² Ð² Ñ‡Ð°Ñ€Ñ‚Ðµ
    const trackSelectors = [
