        console.log(`ðŸ“Š ÐÐ°Ð¹Ð´ÐµÐ½Ñ‹ Ñ‚Ñ€ÐµÐºÐ¸ Ñ ÑÐµÐ»ÐµÐºÑ‚Ð¾Ñ€Ð¾Ð¼: ${selector} (${trackElements.length} ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð²)`);

        trackElements.each((index, element) => {
