  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ Ñ‚Ñ€ÐµÐºÐµ Ð¸Ð· DOM ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð°
  private extractTrackFromElement($: cheerio.CheerioAPI, element: cheerio.Cheerio<any>, position: number): ParsedTrack | null {
    try {
      // Ð’Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ñ‹Ðµ ÑÐµÐ»ÐµÐºÑ‚Ð¾Ñ€Ñ‹ Ð´Ð»Ñ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð°
      const artistSelectors = [
        '.artist', '.track-artist', '.song-artist', '.performer',
        '[class*="artist"]', '[class*="performer"]',
        '.track-info .artist', '.song-info .artist'
      ];

      // Ð’Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ñ‹Ðµ ÑÐµÐ»ÐµÐºÑ‚Ð¾Ñ€Ñ‹ Ð´Ð»Ñ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ñ
      const titleSelectors = [
        '.title', '.track-title', '.song-title', '.track-name', '.song-name',
        '[class*="title"]', '[class*="name"]',
        '.track-info .title', '.song-info .title'
      ];

      let artist = '';
      let title = '';

      // Ð˜Ñ‰ÐµÐ¼ Ð°Ñ€Ñ‚Ð¸ÑÑ‚Ð°
      for (const selector of artistSelectors) {
        const artistElement = element.find(selector).first();
        if (artistElement.length > 0) {
          artist = artistElement.text().trim();
          break;
        }
      }

      // Ð˜Ñ‰ÐµÐ¼ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ðµ
      for (const selector of titleSelectors) {
        const titleElement = element.find(selector).first();
        if (titleElement.length > 0) {
          title = titleElement.text().trim();
          break;
        }
      }

      // ÐÐ»ÑŒÑ‚ÐµÑ€Ð½Ð°Ñ‚Ð¸Ð²Ð½Ñ‹Ð¹ ÑÐ¿Ð¾ÑÐ¾Ð± - Ð¸Ñ‰ÐµÐ¼ Ð² Ð°Ñ‚Ñ€Ð¸Ð±ÑƒÑ‚Ð°Ñ… data-*
      if (!artist || !title) {
        const dataArtist = element.attr('data-artist') || element.find('[data-artist]').first().attr('data-artist');
        const dataTitle = element.attr('data-title') || element.find('[data-title]').first().attr('data-title');

        if (dataArtist) artist = dataArtist;
        if (dataTitle) title = dataTitle;
      }

      // Ð•ÑÐ»Ð¸ Ð²ÑÐµ ÐµÑ‰Ðµ Ð½Ðµ Ð½Ð°ÑˆÐ»Ð¸, Ð¿Ñ€Ð¾Ð±ÑƒÐµÐ¼ Ñ€Ð°ÑÐ¿Ð°Ñ€ÑÐ¸Ñ‚ÑŒ Ð¸Ð· Ð¾Ð±Ñ‰ÐµÐ³Ð¾ Ñ‚ÐµÐºÑÑ‚Ð°
      if (!artist || !title) {
        const fullText = element.text().trim();
        const match = fullText.match(/^(.+?)\s*[-â€“â€”]\s*(.+)$/);

        if (match) {
          artist = match[1].trim();
          title = match[2].trim();
        }
      }

      if (artist && title) {
        return {
          artist: this.cleanArtistName(artist),
          title: title.trim(),
          position
        };
      }

      return null;

    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¸Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ð¸ Ñ‚Ñ€ÐµÐºÐ°:', error);
      return null;
    }
  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ Ñ‚Ñ€ÐµÐºÐ¾Ð² Ð¸Ð· JSON-LD Ñ€Ð°Ð·Ð¼ÐµÑ‚ÐºÐ¸
  private extractTracksFromJsonLd($: cheerio.CheerioAPI): ParsedTrack[] {
    const tracks: ParsedTrack[] = [];

    try {
      $('script[type="application/ld+json"]').each((_, element) => {
        try {
          const jsonData = JSON.parse($(element).html() || '');

          // Ð˜Ñ‰ÐµÐ¼ ÑÑ‚Ñ€ÑƒÐºÑ‚ÑƒÑ€Ñ‹, ÐºÐ¾Ñ‚Ð¾Ñ€Ñ‹Ðµ Ð¼Ð¾Ð³ÑƒÑ‚ ÑÐ¾Ð´ÐµÑ€Ð¶Ð°Ñ‚ÑŒ Ñ‚Ñ€ÐµÐºÐ¸
          if (jsonData['@type'] === 'MusicPlaylist' && jsonData.track) {
            jsonData.track.forEach((track: any, index: number) => {
              if (track.byArtist && track.name) {
                tracks.push({
                  artist: this.cleanArtistName(track.byArtist.name || track.byArtist),
                  title: track.name,
                  position: index + 1
                });
              }
            });
          }

        } catch (jsonError) {
          // Ð˜Ð³Ð½Ð¾Ñ€Ð¸Ñ€ÑƒÐµÐ¼ Ð½ÐµÐ²Ð°Ð»Ð¸Ð´Ð½Ñ‹Ð¹ JSON
        }
      });

    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ðµ JSON-LD:', error);
    }

    return tracks;
  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ñ Ñ‡Ð°Ñ€Ñ‚Ð°
  private extractChartName($: cheerio.CheerioAPI): string {
    const titleSelectors = [
      'h1', '.chart-title', '.page-title', '.title',
      '[class*="chart"] [class*="title"]',
      'title'
    ];

    for (const selector of titleSelectors) {
      const element = $(selector).first();
      if (element.length > 0) {
        const text = element.text().trim();
        if (text && !text.toLowerCase().includes('mts music')) {
          return text;
        }
      }
    }

    return 'Ð§Ð°Ñ€Ñ‚ MTS Music';
  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ñ Ð¿Ð»ÐµÐ¹Ð»Ð¸ÑÑ‚Ð°
  private extractPlaylistName($: cheerio.CheerioAPI): string {
    const titleSelectors = [
      '.playlist-title', '.playlist-name', 'h1', '.page-title', '.title',
      '[class*="playlist"] [class*="title"]',
      'title'
    ];

    for (const selector of titleSelectors) {
      const element = $(selector).first();
      if (element.length > 0) {
        const text = element.text().trim();
        if (text && !text.toLowerCase().includes('mts music')) {
          return text;
        }
      }
    }

    return 'ÐŸÐ»ÐµÐ¹Ð»Ð¸ÑÑ‚ MTS Music';
  }

  // Ð˜Ð·Ð²Ð»ÐµÑ‡ÐµÐ½Ð¸Ðµ Ð¾Ð¿Ð¸ÑÐ°Ð½Ð¸Ñ Ð¿Ð»ÐµÐ¹Ð»Ð¸ÑÑ‚Ð°
  private extractPlaylistDescription($: cheerio.CheerioAPI): string | undefined {
    const descSelectors = [
      '.playlist-description', '.description', '.about',
      '[class*="playlist"] [class*="description"]',
      'meta[name="description"]'
    ];

    for (const selector of descSelectors) {
      const element = $(selector).first();
      if (element.length > 0) {
        const text = selector === 'meta[name="description"]' 
          ? element.attr('content') 
          : element.text().trim();

        if (text && text.length > 10) {
          return text;
        }
      }
    }

    return undefined;
  }
}

