  console.log('ðŸ“‹ Ð¢ÐµÑÑ‚Ð¸Ñ€ÑƒÐµÐ¼ ÑƒÐ½Ð¸Ð²ÐµÑ€ÑÐ°Ð»ÑŒÐ½Ñ‹Ð¹ Ð¿Ð°Ñ€ÑÐµÑ€...');
