export function stopScheduler() {
  let stopped = false;

  if (scheduledTask) {
    scheduledTask.stop();
    scheduledTask = null;
    console.log('ðŸ›‘ ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ð¹ Ð¿Ð»Ð°Ð½Ð¸Ñ€Ð¾Ð²Ñ‰Ð¸Ðº Ð¾ÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½');
    stopped = true;
  }

  if (weeklyReleaseDateTask) {
    weeklyReleaseDateTask.stop();
    weeklyReleaseDateTask = null;
    console.log('ðŸ›‘ Ð•Ð¶ÐµÐ½ÐµÐ´ÐµÐ»ÑŒÐ½Ð¾Ðµ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ð´Ð°Ñ‚ Ð¾ÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½Ð¾');
